// const imageLoader = new ImageLoader('LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ');
import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa';
import axios from 'axios';
import './App.css'

class UnsplashImageLoader {
  constructor(apiKey) {
    this.apiKey = apiKey;
  }

  async getRandomImage() {
    try {
      const response = await axios.get('https://api.unsplash.com/photos/random', {
        headers: {
          Authorization: `Client-ID ${this.apiKey}`,
        },
      });
      return response.data.urls.regular;
    } catch (error) {
      console.error('Error fetching image:', error);
    }
    return null;
  }
}

class ShareButton {
  constructor(url, imageUrl) {
    this.url = url;
    this.imageUrl = imageUrl;
  }

  render() {
    return (
      <div className="share-buttons">
        <FacebookShareButton url={this.url} quote="Check out this random image">
          <FaFacebook size={30} className="fb" />
        </FacebookShareButton>
        <TwitterShareButton url={this.url} title="Check out this random image">
          <FaTwitter size={30} className="tt" />
        </TwitterShareButton>
        <WhatsappShareButton url={this.url} title="Check out this random image: ">
          <FaWhatsapp size={30} className="wa" />
        </WhatsappShareButton>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      image: '',
      loading: true,
    };

    this.imageLoader = new UnsplashImageLoader('LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ');
  }

  componentDidMount() {
    this.fetchRandomImage();
  }

  async fetchRandomImage() {
    try {
      const randomImage = await this.imageLoader.getRandomImage();
      this.setState({ image: randomImage, loading: false });
    } catch (error) {
      console.error('Error fetching image:', error);
    }
  }

  render() {
    const { image } = this.state;

    const title = 'Random';
    const description = 'Anything';

    const shareUrl = window.location.href;

    return (
      <>
        <Helmet>
    <meta name="twitter:card" content="summary_large_image" />
    <meta name="twitter:title" content={title} />
    <meta name="twitter:description" content={description} />
    <meta name="twitter:image" content={image} />

    {/* Facebook */}
    <meta property="og:title" content={title} />
    <meta property="og:description" content={description} />
    <meta property="og:image" content={image} />
    <meta property="og:image:secure_url" content={image} />

    {/* WhatsApp */}
    <meta property="og:title" content={title} />
    <meta property="og:description" content={description} />
    <meta property="og:image" content={image} />
    <meta property="og:image:secure_url" content={image} />
        </Helmet>
        <div className="container">
         
              <img src={image} alt="Random" className="image" />
              {new ShareButton(shareUrl).render()}         
          </div>
      </>
    );
  }
}

export default App;

