// const imageLoader = new ImageLoader('LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ')
    
    import './App.css'
    import React, { useState, useEffect } from 'react';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa';
import axios from 'axios';

const ShareButtons = ({ shareUrl, shareImage }) => (
  <div className="share-buttons">
    <FacebookShareButton url={shareUrl} quote="Check out this random image">
      <FaFacebook size={30} className="fb" />
    </FacebookShareButton>
    <TwitterShareButton url={shareUrl} title="Random Image" via="your_twitter_handle">
      <FaTwitter size={30} className="tt" />
    </TwitterShareButton>
    <WhatsappShareButton url={shareUrl} title="Check out this random image: ">
      <FaWhatsapp size={30} className="wa" />
    </WhatsappShareButton>
  </div>
);

const App = () => {
  const [image, setImage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://api.unsplash.com/photos/random', {
          headers: {
            Authorization: 'Client-ID LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ',
          },
        });
        setImage(response.data.urls.regular);
      } catch (error) {
        console.error('Error fetching image:', error);
      }
    };

    fetchData();
  }, []);

  const shareUrl = window.location.href
  const shareImage = image;

  return (
    <div className="container">
      <head>
        <title>Random Image</title>
        <meta name="description" content="Check out this random image" />
        <meta property="og:title" content="Random Image" />
        <meta property="og:description" content="Check out this random image" />
        <meta property="og:image" content={shareImage} />
        <meta property="og:image:secure_url" content={shareImage} />
        <meta property="og:type" content="website" />
        <meta property="og:url" content={shareUrl} />
      </head>
      {image && <img src={image} alt="Random" className="image" />}
      <ShareButtons shareUrl={shareUrl} shareImage={shareImage} />
    </div>
  );
};


export default App;
