import "./App.css"
import React, { useState, useEffect } from 'react';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa';

const getRandomImage = async () => {
  const response = await fetch('https://api.unsplash.com/photos/random?client_id=LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ');
  const data = await response.json();
  return data.urls.regular;
};

const ShareButton = ({ shareUrl, shareText }) => {
  return (
    <div className="share-buttons">
    <FacebookShareButton url={shareUrl} quote={shareText}>
      <FaFacebook size={24} className="fb" />
    </FacebookShareButton>
    <TwitterShareButton url={shareUrl} title={shareText}>
      <FaTwitter size={24}  className="tt"  />
    </TwitterShareButton>
    <WhatsappShareButton url={shareUrl} title={shareText}>
      <FaWhatsapp size={24}  className="wa"  />
    </WhatsappShareButton>
  </div>
  );
};

const App = () => {
  const [image, setImage] = useState('');

  useEffect(() => {
    const fetchRandomImage = async () => {
      const randomImage = await getRandomImage();
      setImage(randomImage);
      const ogMeta = document.createElement('meta');
      ogMeta.setAttribute('property', 'og:image');
      ogMeta.setAttribute('content', randomImage);
      document.head.appendChild(ogMeta);

      const twitterMeta = document.createElement('meta');
      twitterMeta.setAttribute('name', 'twitter:image');
      twitterMeta.setAttribute('content', randomImage);
      document.head.appendChild(twitterMeta);
    };

    fetchRandomImage();
  }, []);

  const shareUrl = window.location.href;
  const shareText = 'Check out this image!';

  return (
    <div className="container">
      <img src={image} alt="Random" className="image" />
      <ShareButton shareUrl={shareUrl} shareText={shareText} />
    </div>
  );
};

export default App;
