import React, { useState, useEffect } from 'react'
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share'
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa'
import "./App.css"


const ShareButton = ({ shareUrl, shareText, shareImage }) => {
  return (
    <div className="share-buttons">
      <FacebookShareButton url={shareUrl} quote={shareText} imageUrl={shareImage}>
        <FaFacebook size={24} className="fb"/>
      </FacebookShareButton>
      <TwitterShareButton url={shareUrl} title={shareText} imageUrl={shareImage}>
        <FaTwitter size={24} className="tt"/>
      </TwitterShareButton>
      <WhatsappShareButton url={shareUrl} title={shareText} imageUrl={shareImage}>
        <FaWhatsapp size={24}  className="wa"/>
      </WhatsappShareButton>
    </div>
  );
};

const App = () => {
  const [image, setImage] = useState('');

  useEffect(() => {
    const fetchRandomImage = async () => {
      const randomImage = await getRandomImage();
      setImage(randomImage);

      // Update Open Graph meta tags for Facebook
      const ogMeta = document.createElement('meta');
      ogMeta.setAttribute('property', 'og:image');
      ogMeta.setAttribute('content', randomImage);
      document.head.appendChild(ogMeta);

      // Update Twitter Card meta tags for Twitter
      const twitterMeta1 = document.createElement('meta');
      twitterMeta1.setAttribute('name', 'twitter:card');
      twitterMeta1.setAttribute('content', 'summary_large_image');
      document.head.appendChild(twitterMeta1);

      const twitterMeta2 = document.createElement('meta');
      twitterMeta2.setAttribute('name', 'twitter:image');
      twitterMeta2.setAttribute('content', randomImage);
      document.head.appendChild(twitterMeta2);

      // Update meta tags for WhatsApp
      const whatsappMeta = document.createElement('meta');
      whatsappMeta.setAttribute('property', 'og:image');
      whatsappMeta.setAttribute('content', randomImage);
      document.head.appendChild(whatsappMeta);
    };

    fetchRandomImage();
  }, []);

  const shareUrl = window.location.href;
  const shareText = 'Check out this image!';

  return (
    <div className="container">
      <img src={image} alt="Random" className="image" />
      <ShareButton shareUrl={shareUrl} shareText={shareText} shareImage={image} />
    </div>
  );
};

export default App;
