// const imageLoader = new ImageLoader('LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ')

import './App.css'


    import React, { useState, useEffect } from 'react';
    import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
    import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa';
    import axios from 'axios';
    import Head from 'next/head';
    
    class UnsplashImageLoader {
      constructor(apiKey) {
        this.apiKey = apiKey;
      }
    
      async getRandomImage() {
        try {
          const response = await axios.get('https://api.unsplash.com/photos/random', {
            headers: {
              Authorization: `Client-ID ${this.apiKey}`,
            },
          });
          return response.data.urls.regular;
        } catch (error) {
          console.error('Error fetching image:', error);
        }
        return null;
      }
    }
    
    class ShareButtons {
      constructor(shareUrl, shareImage) {
        this.shareUrl = shareUrl;
        this.shareImage = shareImage;
      }
    
      render() {
        return (
          <div className="share-buttons">
            <FacebookShareButton url={this.shareUrl} imageUrl={this.shareImage}>
              <FaFacebook size={30} className="fb" />
            </FacebookShareButton>
            <TwitterShareButton url={this.shareUrl} imageUrl={this.shareImage}>
              <FaTwitter size={30} className="tt" />
            </TwitterShareButton>
            <WhatsappShareButton url={this.shareUrl} imageUrl={this.shareImage}>
              <FaWhatsapp size={30} className="wa" />
            </WhatsappShareButton>
          </div>
        );
      }
    }
    
    class App extends React.Component {
      constructor(props) {
        super(props);
    
        this.state = {
          image: '',
        };
    
        this.unsplashImageLoader = new UnsplashImageLoader('YOUR_UNSPLASH_ACCESS_KEY');
      }
    
      componentDidMount() {
        this.fetchRandomImage();
      }
    
      async fetchRandomImage() {
        try {
          const randomImage = await this.unsplashImageLoader.getRandomImage();
          this.setState({ image: randomImage });
        } catch (error) {
          console.error('Error fetching image:', error);
        }
      }
    
      render() {
        const title = 'Random';
        const description = 'Anything';
    
        const shareUrl = window.location.href
        const shareImage = this.state.image;
    
        return (
          <>
            <Head>
              <title>{title}</title>
              <meta name="description" content={description} />
              <meta property="og:title" content={title} />
              <meta property="og:description" content={description} />
              <meta property="og:image" content={shareImage} />
              <meta property="og:image:secure_url" content={shareImage} />
              <meta property="og:type" content="website" />
              <meta property="og:url" content={shareUrl} />
            </Head>
            <div className="container">
              <img src={this.state.image} alt="Random" className="image" />
              <ShareButtons shareUrl={shareUrl} shareImage={shareImage} />
            </div>
          </>
        );
      }
    }


export default App
