// const imageLoader = new ImageLoader('LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ');

      const apiKey = 'LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ';
import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa';
import axios from 'axios';

class UnsplashImageLoader {
  constructor(apiKey) {
    this.apiKey = apiKey;
  }

  async getRandomImage() {
    try {
      const response = await axios.get('https://api.unsplash.com/photos/random', {
        headers: {
          Authorization: `Client-ID ${this.apiKey}`,
        },
      });
      return response.data.urls.regular;
    } catch (error) {
      console.error('Error fetching image:', error);
    }
    return null;
  }
}

class ShareButton {
  constructor(url) {
    this.url = url;
  }

  render() {
    return (
      <div className="share-buttons">
        <FacebookShareButton url={this.url}>
          <FaFacebook size={30} className="fb" />
        </FacebookShareButton>
        <TwitterShareButton url={this.url}>
          <FaTwitter size={30} className="tt" />
        </TwitterShareButton>
        <WhatsappShareButton url={this.url}>
          <FaWhatsapp size={30} className="wa" />
        </WhatsappShareButton>
      </div>
    );
  }
}

const App = () => {
  const [image, setImage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const imageLoader = new UnsplashImageLoader('YOUR_UNSPLASH_ACCESS_KEY');

    const fetchRandomImage = async () => {
      const randomImage = await imageLoader.getRandomImage();
      setImage(randomImage);
      setLoading(false);
    };

    fetchRandomImage();
  }, []);

  const title = 'Random';
  const description = 'Anything';

  const shareUrl = window.location.href;

  useEffect(() => {
    const metaTags = [
      { name: 'twitter:card', content: 'summary_large_image' },
      { name: 'twitter:title', content: title },
      { name: 'twitter:description', content: description },
      { name: 'twitter:image', content: image },
      { property: 'og:title', content: title },
      { property: 'og:description', content: description },
      { property: 'og:image', content: image },
      { property: 'og:image:secure_url', content: image },
    ];

    metaTags.forEach((metaTag) => {
      const element = document.querySelector(`meta[name="${metaTag.name}"], meta[property="${metaTag.property}"]`);
      if (element) {
        element.setAttribute('content', metaTag.content);
      } else {
        const newElement = document.createElement('meta');
        newElement.setAttribute(metaTag.name ? 'name' : 'property', metaTag.name || metaTag.property);
        newElement.setAttribute('content', metaTag.content);
        document.head.appendChild(newElement);
      }
    });
  }, [title, description, image]);

  const shareButton = new ShareButton(shareUrl);

  return (
    <div className="container">
      {!loading ? (
        <>
          <img src={image} alt="Random" className="image" />
          {shareButton.render()}
        </>
      ) : (
        <p>Loading image...</p>
      )}
    </div>
  );
};

export default App;
