import React, { useState, useEffect } from 'react';
import {FacebookShareButton, TwitterShareButton, WhatsappShareButton} from "react-share";
import "./App.css"

const getRandomImage = async () => {
  const response = await fetch('https://picsum.photos');
  const data = await response.json();
  return data.urls.regular;
};

const ShareButton = ({ image }) => {
  const shareOnFacebook = () => {
    const url = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(window.location.href)}`;
    window.open(url, '_blank');
  };

  const shareOnTwitter = () => {
    const url = `https://twitter.com/intent/tweet?url=${encodeURIComponent(window.location.href)}&text=${encodeURIComponent('Check out this image!')}`;
    window.open(url, '_blank');
  };

  const shareOnWhatsApp = () => {
    const url = `https://wa.me/?text=${encodeURIComponent(`${window.location.href}\nCheck out this image!`)}`;
    window.open(url, '_blank');
  };

  return (
    <div className="share-buttons">
      FacebookShareButton, TwitterShareButton, WhatsappShareButton
      <button onClick={shareOnFacebook}>Share on Facebook</button>
      <button onClick={shareOnTwitter}>Share on Twitter</button>
      <button onClick={shareOnWhatsApp}>Share on WhatsApp</button>
    </div>
  );
};

const App = () => {
  const [image, setImage] = useState('');

  useEffect(() => {
    const fetchRandomImage = async () => {
      const randomImage = await getRandomImage();
      setImage(randomImage);

      // Update Open Graph and Twitter Card meta tags
      const metaTags = document.getElementsByTagName('meta');

      for (let i = 0; i < metaTags.length; i++) {
        const tag = metaTags[i];
        const property = tag.getAttribute('property');
        const name = tag.getAttribute('name');

        if (property === 'og:image' || name === 'twitter:image') {
          tag.setAttribute('content', randomImage);
        }
      }
    };

    fetchRandomImage();
  }, []);

  return (
    <div className="container">
      <img src={image} alt="Random" className="image" />
      <ShareButton image={image} />
    </div>
  );
};

export default App;
