// const imageLoader = new ImageLoader('LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ');

import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa';
import axios from 'axios';

const PexelsImageLoader = {
  async getRandomImage(apiKey) {
    try {
      const response = await axios.get('https://api.pexels.com/v1/curated', {
        headers: {
          Authorization: apiKey,
        },
      });
      const { photos } = response.data;
      if (photos && photos.length > 0) {
        return photos[0].src.large;
      }
    } catch (error) {
      console.error('Error fetching image:', error);
    }
    return null;
  },
};

const App = () => {
  const [image, setImage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRandomImage = async () => {
      const imageLoader = new UnsplashImageLoader('YOUR_UNSPLASH_ACCESS_KEY');
      const randomImage = await imageLoader.getRandomImage();
      setImage(randomImage);
      setLoading(false);
    };

    fetchRandomImage();
  }, []);

  const title = 'Random';
  const description = 'Anything';

  const shareUrl = window.location.href;

  return (
    <div className="container">
      <Helmet>
        {/* Twitter */}
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content={title} />
        <meta name="twitter:description" content={description} />
        <meta name="twitter:image" content={image} />

        {/* Facebook */}
        <meta property="og:title" content={title} />
        <meta property="og:description" content={description} />
        <meta property="og:image" content={image} />
        <meta property="og:image:secure_url" content={image} />

        {/* WhatsApp */}
        <meta property="og:title" content={title} />
        <meta property="og:description" content={description} />
        <meta property="og:image" content={image} />
        <meta property="og:image:secure_url" content={image} />
      </Helmet>
      {image ? (
        <>
          <img src={image} alt="Random" className="image" />
          <div className="share-buttons">
            <FacebookShareButton url={shareUrl}>
              <FaFacebook size={30} className="fb" />
            </FacebookShareButton>
            <TwitterShareButton url={shareUrl}>
              <FaTwitter size={30} className="tt" />
            </TwitterShareButton>
            <WhatsappShareButton url={shareUrl}>
              <FaWhatsapp size={30} className="wa" />
            </WhatsappShareButton>
          </div>
        </>
      ) : (
        <p>Loading image...</p>
      )}
    </div>
  );
};

export default App;
