import React, { useState, useEffect } from 'react'
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share'
import { FaFacebook, FaTwitter, FaWhatsapp } from 'react-icons/fa'
import "./App.css"

const getRandomImage = async () => {
  const response = await fetch('https://api.unsplash.com/photos/random?client_id=LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ')
  const data = await response.json()
  return data.urls.regular
} 

const ShareButton = ({ shareUrl, shareText, shareImage }) => {
  return (
    <div className="share-buttons">
      <FacebookShareButton url={shareUrl} quote={shareText} imageUrl={shareImage}>
        <FaFacebook size={24} className="fb"/>
      </FacebookShareButton>
      <TwitterShareButton url={shareUrl} title={shareText} imageUrl={shareImage}>
        <FaTwitter size={24} className="tt"/>
      </TwitterShareButton>
      <WhatsappShareButton url={shareUrl} title={shareText} imageUrl={shareImage}>
        <FaWhatsapp size={24} className="wa" />
      </WhatsappShareButton>
    </div>
  );
};
const App = () => {
  const [image, setImage] = useState('');

  useEffect(() => {
    const fetchRandomImage = async () => {
      const randomImage = await getRandomImage();
      setImage(randomImage);
    };

    fetchRandomImage();
  }, []);

  return (
    <div className="container">
      <img src={image} alt="Random" className="image" />
      <ShareButton shareImage={image} />
    </div>
  );
};

export default App;
