import "./App.css"
import React, { useState, useEffect } from 'react';
import { FacebookIcon,
 
  TumblrIcon,
  TwitterIcon,
  ViberIcon,
  VKIcon,
  WeiboIcon,
  WhatsappIcon,
  WorkplaceIcon } from 'react-share';

const getRandomImage = async () => {
  const response = await fetch('https://api.unsplash.com/photos/random?client_id=LqzS6iYTqv2QxPqhQu4fp7Fzl9pmyhXbWOBxjpiG7NQ');
  const data = await response.json();
  return data.urls.regular;
};

const ShareButton = ({ shareUrl, shareText }) => {
  return (
    <div className="share-buttons">
      <FacebookShareButton url={shareUrl} quote={shareText}>
        <button>Share on Facebook</button>
      </FacebookShareButton>
      <TwitterShareButton url={shareUrl} title={shareText}>
        <button>Share on Twitter</button>
      </TwitterShareButton>
      <WhatsappShareButton url={shareUrl} title={shareText}>
        <button>Share on WhatsApp</button>
      </WhatsappShareButton>
    </div>
  );
};

const App = () => {
  const [image, setImage] = useState('');

  useEffect(() => {
    const fetchRandomImage = async () => {
      const randomImage = await getRandomImage();
      setImage(randomImage);
    };

    fetchRandomImage();
  }, []);

  const shareUrl = window.location.href;
  const shareText = 'Check out this image!';

  return (
    <div className="container">
      <img src={image} alt="Random" className="image" />
      <ShareButton shareUrl={shareUrl} shareText={shareText} />
    </div>
  );
};

export default App;
